import React, { useState, useEffect } from "react";

function Playlist() {
  const [songs, setSongs] = useState([]);
  const [updateSong, setUpdateSong] = useState(false);

  useEffect(() => {
    fetch("/songs")
      .then((res) => res.json())
      .then((songData) => setSongs(songData));
    console.log("updated song");
  }, [updateSong]);

  function handleDelete(song) {
    fetch(`/songs/${song.id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    });
    console.log("delete song");
    setUpdateSong(!updateSong);
  }

  const mapSongs = songs.map((song) => {
    return (
      <div className="song_container_card">
        <h1 className="song_title">{song.title}</h1>
        <img src={song.image} />
        <div>{song.artist}</div>
        <div>{song.duration}</div>
        <button onClick={() => handleDelete(song)}>Remove From Playlist</button>
      </div>
    );
  });

  const [update, setUpdate] = useState("");
  const [title, setTitle] = useState("");
  const [playlist, setPlaylist] = useState({});

  const handleChange = (e) => {
    setTitle(e.target.value);
  };

  function handlePlaylist(e) {
    e.preventDefault();
    console.log("test");
  }

  useEffect(() => {
    fetch("/playlist", {
      method: "create",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(playlist),
    });
  }, [update]);

  function handleClick() {
    setPlaylist({
      title: "test",
      duration: "5.00",
      image: "test",
      song: { songs },
    });
    setUpdate(!update);
  }

  return (
    <div className="song_container">
      {mapSongs}
      <form className="search_menu" id="search" onSubmit={handlePlaylist}>
        <input
          type="text"
          id="Title"
          placeholder="Playlist Title"
          name="s"
          onChange={handleChange}
        />
        <button onClick={handleClick} className="button" type="submit">
          Create Playlist
        </button>
      </form>
    </div>
  );
}

export default Playlist;
