import React, { useState, useEffect } from "react";

function Playlist() {
  const [songs, setSongs] = useState([]);
  const [updateSong, setUpdateSong] = useState(false);

  const [title, setTitle] = useState("");

  const [playlist, setPlaylist] = useState({});

  useEffect(() => {
    fetch("/songs")
      .then((res) => res.json())
      .then((songData) => setSongs(songData));
    console.log("updated song");
  }, [updateSong]);

  function handleDelete(song) {
    fetch(`/songs/${song.id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    });
    console.log("delete song");
    setUpdateSong(!updateSong);
  }

  const mapSongs = songs.map((song) => {
    return (
      <div className="song_container_card">
        <h1 className="song_title">{song.title}</h1>
        <img src={song.image} />
        <div>{song.artist}</div>
        <div>{song.duration}</div>
        <button onClick={() => handleDelete(song)}>Remove From Playlist</button>
      </div>
    );
  });

  const handleChange = (e) => {
    setTitle(e.target.value);
  };

  function handleSearch(e) {
    e.preventDefault();

    fetch("/playlist", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        password,
      }),
    }).then((r) => {
      setIsLoading(false);
      if (r.ok) {
        r.json().then((user) => onLogin(user));
      } else {
        r.json().then((err) => setErrors(err.errors));
      }
    });
  }

  return (
    <div className="song_container">
      {mapSongs}
      <form className="search_menu" id="search" onSubmit={handleSearch}>
        <input
          type="text"
          id="Title"
          placeholder="Playlist Title"
          name="s"
          onChange={handleChange}
        />
        <button className="button" type="submit">
          Create Playlist
        </button>
      </form>
    </div>
  );
}

export default Playlist;
