import React, { useEffect, useState } from "react";
import SongCard from "./SongCard";
import "./Search.css";

const SearchBar = () => {
  const [search, setSearch] = useState("");
  const [results, setResults] = useState([]);
  const [update, setUpdate] = useState(false);

  const deezer = `https://api.deezer.com/search?q=${search}&redirect_uri=http%253A%252F%252Fguardian.mashape.com%252Fcallback&index=25
  `;

  function handleSearch(e) {
    e.preventDefault();
    setUpdate(!update);
  }

  useEffect(() => {
    if (search == "") {
    } else {
      fetch(deezer)
        .then((res) => res.json())
        .then((songData) => setResults(songData.data));
    }
  }, [update]);
  // console.log(results);
  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  return (
    // <form className="search_menu" id="search" onSubmit={handleSearch}>
    //   <input
    //     type="text"
    //     id="search"
    //     placeholder="Search..."
    //     name="s"
    //     onChange={handleChange}
    //   />
    //   <button className="button" type="submit">
    //     Search
    //   </button>
    //   <SongCard results={results} />
    // </form>

    <div class="searchBox">
      <input class="searchInput" type="text" name="" placeholder="Search" />
      <button class="searchButton" href="#">
        <i class="material-icons">search</i>
      </button>
      <SongCard results={results} />
    </div>
  );
};

export default SearchBar;
